import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const Register = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    whatsapp: '',
    address: '',
    zipCode: '',
    port: '',
    workClass: '',
    documents: [],
    declaration: false
  });
  const [emailVerified, setEmailVerified] = useState(false);
  const [whatsappVerified, setWhatsappVerified] = useState(false);
  const [emailOtp, setEmailOtp] = useState('');
  const [whatsappOtp, setWhatsappOtp] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [vendorCode, setVendorCode] = useState('');
  const [requiresEmailConfirmation, setRequiresEmailConfirmation] = useState(false);
  const navigate = useNavigate();

  const workClasses = [
    { id: 'seller', name: 'Seller', icon: '🏪' },
    { id: 'buyer', name: 'Buyer', icon: '🛒' },
    { id: 'surveyor', name: 'Surveyor', icon: '🔍' },
    { id: 'insurance', name: 'Insurance Agent', icon: '🛡️' },
    { id: 'transporter', name: 'Transporter', icon: '🚚' },
    { id: 'logistics', name: 'Logistics', icon: '📦' },
    { id: 'cha', name: 'CHA', icon: '🏛️' },
    { id: 'hr', name: 'HR', icon: '👥' },
    { id: 'admin', name: 'Admin', icon: '⚙️' }
  ];

  const ports = [
    'Mumbai Port',
    'Chennai Port',
    'JNPT',
    'Kolkata Port',
    'Vishakhapatnam Port',
    'Not Applicable'
  ];

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleDocumentUpload = (e) => {
    // In a real app, this would handle file uploads
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      documents: [...prev.documents, ...files]
    }));
  };

  const sendEmailOtp = async () => {
    if (!formData.email) {
      setError('Please enter your email first');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch('/api/send-email-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: formData.email }),
      });
      
      const result = await response.json();
      
      if (response.ok) {
        alert(`OTP sent to ${formData.email}. Please check the server console for the OTP code.`);
        // In a real app, you would not show the OTP. This is just for testing.
        // For testing purposes, the OTP is logged to the server console
      } else {
        setError(result.error || 'Failed to send OTP');
      }
    } catch (err) {
      console.error('Error sending email OTP:', err);
      setError('Failed to send OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const verifyEmailOtp = async () => {
    if (!formData.email || !emailOtp) {
      setError('Please enter both email and OTP');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch('/api/verify-email-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: formData.email, otp: emailOtp }),
      });
      
      const result = await response.json();
      
      if (response.ok) {
        setEmailVerified(true);
        setError('');
        alert('Email verified successfully!');
      } else {
        setError(result.error || 'Invalid OTP. Please try again.');
      }
    } catch (err) {
      console.error('Error verifying email OTP:', err);
      setError('Failed to verify OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const sendWhatsappOtp = async () => {
    if (!formData.whatsapp) {
      setError('Please enter your WhatsApp number first');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch('/api/send-sms-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ phoneNumber: formData.whatsapp }),
      });
      
      const result = await response.json();
      
      if (response.ok) {
        alert(`OTP sent to WhatsApp ${formData.whatsapp}. Please check the server console for the OTP code.`);
        // In a real app, you would not show the OTP. This is just for testing.
        // For testing purposes, the OTP is logged to the server console
      } else {
        setError(result.error || 'Failed to send OTP');
      }
    } catch (err) {
      console.error('Error sending WhatsApp OTP:', err);
      setError('Failed to send OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const verifyWhatsappOtp = async () => {
    if (!formData.whatsapp || !whatsappOtp) {
      setError('Please enter both WhatsApp number and OTP');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch('/api/verify-sms-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ phoneNumber: formData.whatsapp, otp: whatsappOtp }),
      });
      
      const result = await response.json();
      
      if (response.ok) {
        setWhatsappVerified(true);
        setError('');
        alert('WhatsApp verified successfully!');
      } else {
        setError(result.error || 'Invalid OTP. Please try again.');
      }
    } catch (err) {
      console.error('Error verifying WhatsApp OTP:', err);
      setError('Failed to verify OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate all required fields
    if (!formData.name) {
      setError('Please enter your name/company name');
      return;
    }
    
    if (!formData.email) {
      setError('Please enter your email');
      return;
    }
    
    if (!emailVerified) {
      setError('Please verify your email');
      return;
    }
    
    if (!formData.whatsapp) {
      setError('Please enter your WhatsApp number');
      return;
    }
    
    if (!whatsappVerified) {
      setError('Please verify your WhatsApp number');
      return;
    }
    
    if (!formData.address) {
      setError('Please enter your address');
      return;
    }
    
    if (!formData.port) {
      setError('Please select your nearest port');
      return;
    }
    
    if (!formData.workClass) {
      setError('Please select your work class');
      return;
    }
    
    if (!formData.declaration) {
      setError('Please accept the declaration and consent checkbox');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      // Extract first and last name from the full name
      const nameParts = formData.name.trim().split(' ');
      const firstName = nameParts[0] || '';
      const lastName = nameParts.length > 1 ? nameParts[nameParts.length - 1] : '';
      
      // Prepare data for the API call
      const registrationData = {
        email: formData.email,
        password: 'tempPassword123', // In a real app, this would be a proper password
        firstName: firstName,
        lastName: lastName,
        phone: formData.whatsapp,
        workClass: formData.workClass
      };
      
      console.log('Sending registration data:', registrationData);
      
      // Send registration data to the backend
      const response = await fetch('/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(registrationData),
      });
      
      const result = await response.json();
      console.log('Registration response:', result);
      
      if (response.ok) {
        // Registration successful
        setVendorCode(result.vendorCode);
        setRequiresEmailConfirmation(result.requiresEmailConfirmation);
        
        if (result.requiresEmailConfirmation) {
          alert(`Registration successful! A confirmation email has been sent to ${formData.email}. Please check your email and follow the instructions to complete your registration. Your Vendor Code is: ${result.vendorCode}`);
        } else {
          alert(`Registration successful! Your Vendor Code is: ${result.vendorCode}. You can now login with your email and password.`);
        }
        
        // Redirect to login page
        navigate('/login');
      } else {
        // Registration failed
        setError(result.error || 'Registration failed. Please try again.');
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('An error occurred during registration. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleNextStep1 = () => {
    // Validate step 1 fields before proceeding
    if (!formData.name) {
      setError('Please enter your name/company name');
      return;
    }
    
    if (!formData.email) {
      setError('Please enter your email');
      return;
    }
    
    if (!emailVerified) {
      setError('Please verify your email');
      return;
    }
    
    if (!formData.whatsapp) {
      setError('Please enter your WhatsApp number');
      return;
    }
    
    if (!whatsappVerified) {
      setError('Please verify your WhatsApp number');
      return;
    }
    
    if (!formData.address) {
      setError('Please enter your address');
      return;
    }
    
    if (!formData.port) {
      setError('Please select your nearest port');
      return;
    }
    
    setError('');
    setStep(2);
  };

  const handleNextStep2 = () => {
    // Validate step 2 fields before proceeding
    if (!formData.workClass) {
      setError('Please select your work class');
      return;
    }
    
    setError('');
    setStep(3);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Register for MarsaFyi</h2>
        
        {/* Progress Indicator */}
        <div className="progress-indicator">
          <div className={`step ${step >= 1 ? 'active' : ''}`}>1</div>
          <div className={`step ${step >= 2 ? 'active' : ''}`}>2</div>
          <div className={`step ${step >= 3 ? 'active' : ''}`}>3</div>
        </div>
        
        {error && <div className="error-message">{error}</div>}
        {loading && <div className="loading-message">Processing...</div>}
        {requiresEmailConfirmation && (
          <div className="info-message">
            A confirmation email has been sent to your email address. Please check your inbox and follow the instructions to complete your registration.
          </div>
        )}
        
        <form onSubmit={handleSubmit}>
          {step === 1 && (
            <div className="registration-step">
              <h3>Personal Information</h3>
              <div className="form-group">
                <label htmlFor="name" className="form-label">Name / Company Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  className="form-control"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="email" className="form-label">Email *</label>
                <div className="input-group">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    className="form-control"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                  />
                  <button 
                    type="button" 
                    className="btn btn-outline"
                    onClick={sendEmailOtp}
                    disabled={!formData.email || loading}
                  >
                    {loading ? 'Sending...' : 'Send OTP'}
                  </button>
                </div>
                {formData.email && (
                  <div className="otp-verification">
                    <input
                      type="text"
                      placeholder="Enter OTP"
                      className="form-control"
                      value={emailOtp}
                      onChange={(e) => setEmailOtp(e.target.value)}
                      disabled={emailVerified}
                    />
                    <button 
                      type="button" 
                      className="btn btn-primary"
                      onClick={verifyEmailOtp}
                      disabled={emailVerified || loading}
                    >
                      {loading ? 'Verifying...' : 'Verify'}
                    </button>
                  </div>
                )}
                {emailVerified && <div className="verification-success">✓ Email Verified</div>}
              </div>
              
              <div className="form-group">
                <label htmlFor="whatsapp" className="form-label">WhatsApp Number *</label>
                <div className="input-group">
                  <input
                    type="tel"
                    id="whatsapp"
                    name="whatsapp"
                    className="form-control"
                    value={formData.whatsapp}
                    onChange={handleInputChange}
                    required
                  />
                  <button 
                    type="button" 
                    className="btn btn-outline"
                    onClick={sendWhatsappOtp}
                    disabled={!formData.whatsapp || loading}
                  >
                    {loading ? 'Sending...' : 'Send OTP'}
                  </button>
                </div>
                {formData.whatsapp && (
                  <div className="otp-verification">
                    <input
                      type="text"
                      placeholder="Enter OTP"
                      className="form-control"
                      value={whatsappOtp}
                      onChange={(e) => setWhatsappOtp(e.target.value)}
                      disabled={whatsappVerified}
                    />
                    <button 
                      type="button" 
                      className="btn btn-primary"
                      onClick={verifyWhatsappOtp}
                      disabled={whatsappVerified || loading}
                    >
                      {loading ? 'Verifying...' : 'Verify'}
                    </button>
                  </div>
                )}
                {whatsappVerified && <div className="verification-success">✓ WhatsApp Verified</div>}
              </div>
              
              <div className="form-group">
                <label htmlFor="address" className="form-label">Address *</label>
                <textarea
                  id="address"
                  name="address"
                  className="form-control"
                  value={formData.address}
                  onChange={handleInputChange}
                  rows="3"
                  required
                ></textarea>
              </div>
              
              <div className="form-group">
                <label htmlFor="zipCode" className="form-label">Zip Code</label>
                <input
                  type="text"
                  id="zipCode"
                  name="zipCode"
                  className="form-control"
                  value={formData.zipCode}
                  onChange={handleInputChange}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="port" className="form-label">Nearest Commercially Active Port *</label>
                <select
                  id="port"
                  name="port"
                  className="form-control"
                  value={formData.port}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a port</option>
                  {ports.map(port => (
                    <option key={port} value={port}>{port}</option>
                  ))}
                </select>
              </div>
              
              <div className="form-navigation">
                <button 
                  type="button" 
                  className="btn btn-primary"
                  onClick={handleNextStep1}
                  disabled={loading}
                >
                  Next
                </button>
              </div>
            </div>
          )}
          
          {step === 2 && (
            <div className="registration-step">
              <h3>Select Work Class *</h3>
              <div className="work-classes">
                {workClasses.map(workClass => (
                  <div 
                    key={workClass.id}
                    className={`work-class-card ${formData.workClass === workClass.id ? 'selected' : ''}`}
                    onClick={() => setFormData(prev => ({...prev, workClass: workClass.id}))}
                  >
                    <div className="work-class-icon">{workClass.icon}</div>
                    <div className="work-class-name">{workClass.name}</div>
                  </div>
                ))}
              </div>
              
              {formData.workClass && (
                <div className="form-group">
                  <label className="form-label">Upload Required Documents</label>
                  <input
                    type="file"
                    className="form-control"
                    onChange={handleDocumentUpload}
                    multiple
                  />
                  <div className="document-instructions">
                    {getDocumentInstructions(formData.workClass)}
                  </div>
                </div>
              )}
              
              <div className="form-navigation">
                <button 
                  type="button" 
                  className="btn btn-outline"
                  onClick={() => setStep(1)}
                  disabled={loading}
                >
                  Previous
                </button>
                <button 
                  type="button" 
                  className="btn btn-primary"
                  onClick={handleNextStep2}
                  disabled={loading}
                >
                  Next
                </button>
              </div>
            </div>
          )}
          
          {step === 3 && (
            <div className="registration-step">
              <h3>Declaration and Consent *</h3>
              <div className="declaration-box">
                <p>
                  I accept the <strong>{getRoleName(formData.workClass)}</strong> agreement and 
                  uphold the platform and company's privacy policy.
                </p>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      name="declaration"
                      checked={formData.declaration}
                      onChange={handleInputChange}
                    />
                    I agree to the terms and conditions
                  </label>
                </div>
              </div>
              
              <div className="form-navigation">
                <button 
                  type="button" 
                  className="btn btn-outline"
                  onClick={() => setStep(2)}
                  disabled={loading}
                >
                  Previous
                </button>
                <button 
                  type="submit" 
                  className="btn btn-success"
                  disabled={loading}
                >
                  {loading ? 'Submitting...' : 'Submit Registration'}
                </button>
              </div>
            </div>
          )}
        </form>
        
        <div className="auth-links">
          <p>Already have an account? <a href="/login">Login</a></p>
          <p><a href="/">Back to Main Dashboard</a></p>
        </div>
      </div>
    </div>
  );
};

const getRoleName = (roleId) => {
  const roles = {
    'seller': 'Seller',
