import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import MainDashboard from './components/MainDashboard';
import SellerDashboard from './components/SellerDashboard';
import BuyerDashboard from './components/BuyerDashboard';
import CaptainDashboard from './components/CaptainDashboard';
import AdminDashboard from './components/AdminDashboard';
import HrDashboard from './components/HrDashboard';
import AccountantDashboard from './components/AccountantDashboard';
import ArbitratorDashboard from './components/ArbitratorDashboard';
import SurveyorDashboard from './components/SurveyorDashboard';
import InsuranceAgentDashboard from './components/InsuranceAgentDashboard';
import TransporterDashboard from './components/TransporterDashboard';
import LogisticsDashboard from './components/LogisticsDashboard';
import CHADashboard from './components/CHADashboard';
import RoleSwitcher from './components/RoleSwitcher';
import LoadingScreen from './components/LoadingScreen';
import { AuthProvider, useAuth } from './contexts/AuthContext';

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time for animation
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Routes>
            <Route path="/" element={<MainDashboard />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/dashboard/seller" element={
              <ProtectedRoute>
                <SellerDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/buyer" element={
              <ProtectedRoute>
                <BuyerDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/captain" element={
              <ProtectedRoute>
                <CaptainDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/admin" element={
              <ProtectedRoute>
                <AdminDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/hr" element={
              <ProtectedRoute>
                <HrDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/accountant" element={
              <ProtectedRoute>
                <AccountantDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/arbitrator" element={
              <ProtectedRoute>
                <ArbitratorDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/surveyor" element={
              <ProtectedRoute>
                <SurveyorDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/insurance" element={
              <ProtectedRoute>
                <InsuranceAgentDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/transporter" element={
              <ProtectedRoute>
                <TransporterDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/logistics" element={
              <ProtectedRoute>
                <LogisticsDashboard />
              </ProtectedRoute>
            } />
            <Route path="/dashboard/cha" element={
              <ProtectedRoute>
                <CHADashboard />
              </ProtectedRoute>
            } />
            <Route path="/role-switch" element={
              <ProtectedRoute>
                <RoleSwitcher />
              </ProtectedRoute>
            } />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

function ProtectedRoute({ children }) {
  const { currentUser } = useAuth();
  
  // In a real app, you would check authentication status
  // For now, we'll just render the children
  return children;
}

export default App;
