import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const MainDashboard = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPort, setSelectedPort] = useState('All Ports');
  const navigate = useNavigate();

  // Mock data for catalogs
  const catalogs = [
    {
      id: 1,
      title: 'Premium Electronics',
      seller: 'VEND-23-ABC123',
      port: 'Mumbai Port',
      category: 'Electronics',
      description: 'High-quality electronic components for industrial use',
      likes: 124,
      image: '/placeholder.jpg'
    },
    {
      id: 2,
      title: 'Organic Textiles',
      seller: 'VEND-23-XYZ789',
      port: 'Chennai Port',
      category: 'Textiles',
      description: 'Organic cotton fabrics for sustainable fashion',
      likes: 89,
      image: '/placeholder.jpg'
    },
    {
      id: 3,
      title: 'Industrial Machinery',
      seller: 'VEND-23-DEF456',
      port: 'JNPT',
      category: 'Machinery',
      description: 'Heavy-duty machinery for manufacturing processes',
      likes: 67,
      image: '/placeholder.jpg'
    }
  ];

  const ports = ['All Ports', 'Mumbai Port', 'Chennai Port', 'JNPT', 'Kolkata Port', 'Vishakhapatnam Port'];

  const handleRFQ = (catalogId) => {
    // In a real app, this would check if user is logged in
    // For now, we'll redirect to login
    navigate('/login');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="main-title">Welcome to Marsa Mart</h1>
        <p className="description">Global B2B Trade Platform</p>
        
        <div className="search-container">
          <input
            type="text"
            placeholder="Search catalogs..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select
            value={selectedPort}
            onChange={(e) => setSelectedPort(e.target.value)}
            className="port-selector"
          >
            {ports.map(port => (
              <option key={port} value={port}>{port}</option>
            ))}
          </select>
          <button className="btn btn-primary">Search</button>
        </div>
        
        <div className="catalogs-section">
          <h2>Featured Catalogs</h2>
          <div className="catalogs-grid">
            {catalogs.map(catalog => (
              <div key={catalog.id} className="catalog-card">
                <img src={catalog.image} alt={catalog.title} className="catalog-image" />
                <div className="catalog-info">
                  <h3>{catalog.title}</h3>
                  <p className="catalog-seller">Seller: {catalog.seller}</p>
                  <p className="catalog-port">Port: {catalog.port}</p>
                  <p className="catalog-description">{catalog.description}</p>
                  <div className="catalog-actions">
                    <button className="btn btn-outline" onClick={() => handleRFQ(catalog.id)}>
                      RFQ
                    </button>
                    <div className="likes">
                      <span>üëç {catalog.likes}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        
        <div className="cta-section">
          <h2>Join MarsaFyi Today!</h2>
          <p>Register to access full features and connect with global buyers and sellers</p>
          <div className="cta-buttons">
            <button className="btn btn-primary" onClick={() => navigate('/register')}>
              Register Now
            </button>
            <button className="btn btn-outline" onClick={() => navigate('/login')}>
              Login
            </button>
          </div>
        </div>
      </header>
    </div>
  );
};

export default MainDashboard;
